import React,{useState,useEffect} from 'react'
import {Row,Col,Card,CardBody,CardImg,Tooltip} from 'reactstrap';
import "bootstrap/dist/css/bootstrap.min.css";
import Suits from "./images/suits.jpeg"
import Pro from "./images/pro.jpeg"
import Expand from './Expand'


function Expand_onclick_div() {
    const [isShown,setIsShown] = useState(false)

    const [initial,finalimage] = useState(Suits)
    const [initialvalue,finalvalue] = useState(false)
    
    

    // const [tooltipOpen, setTooltipOpen] = React.useState(false);

    const handleMouseEnter = e => {  
        finalimage(Pro);
        
      }
      const handleMouseLeave = e => {
        finalimage(Suits);
       
        
      }

     

    // const handleMouseEnter = e => {  
    //     setIsShown(true)
    //   }
    //   const handleMouseLeave = e => {
    //     setIsShown(false)
    //   }


  return (
    <div>
         <Row>
            <Col>
            <Card id="testID" onMouseEnter={() => setIsShown(true)}
                  onMouseLeave={() => setIsShown(false)} >

                {/* <Tooltip isOpen={tooltipOpen}
                // delay={{ show:1000, hide: 2000 }}
                target="testID"
                placement="auto"
                toggle={() => setTooltipOpen(!tooltipOpen)}>
                Sample ToolTip Information...
                </Tooltip> */}

                <CardImg variant="top" src={initial} />
                <CardBody> 
                  
                </CardBody>
                
                {isShown && (
                 <div>
                <a >
                    <img src={Pro}  onMouseEnter={handleMouseEnter} style={{height:"70px",width:"70px"}} /> </a>
                <a>  <img src={Suits} onMouseEnter={handleMouseLeave} style={{height:"70px",width:"70px"}}/>  </a>
                <Expand/>
                 
                 </div>
                 )}

                 {/* {initialvalue && (
                 <div>
                price  55$
                 </div>
                 )} */}

                


                 <h6>jackets and coats</h6>
                 <a>fashonable and luxurious</a>
            </Card>
            </Col>
            <Col></Col>
            <Col></Col>
         </Row>

    </div>
  )
}

export default Expand_onclick_div
